window.onload=()=>{const e=document.querySelector("#newGameButton"),o=document.querySelector("#codeDisplay"),t=document.querySelector("#codeInput"),a=document.querySelector("#joinGameButton"),n=document.querySelector("#joinResultDisplay");e.onclick=async()=>{const e=await fetch("/newGame",{method:"post",headers:{Accept:"application/json"}}),t=await e.json();o.innerHTML=t.code},a.onclick=async()=>{const e=t.value.toUpperCase(),o=await fetch(`/joinGame?code=${e}`,{method:"post",headers:{Accept:"application/json"}}),a=await o.json();200===o.status?n.innerHTML="Game joined! "+(a.player1Scribbles?"Wait for the other player to make a scribble.":"Make a scribble!"):n.innerHTML=a.message}};